import React, { Component, useState } from 'react';
import {Carousel, CarouselItem, CarouselControl, CarouselIndicators, CarouselCaption} from 'reactstrap';


const items = 
[{src: '/imagenes/img1.jpeg', altText: 'Slide 1', caption: 'Slide 1'},
{src: '/imagenes/img2.jpeg', altText: 'Slide 2', caption: 'Slide 2'},
{src: '/imagenes/img3.png', altText: 'Slide 3', caption: 'Slide 3'}];

const App = (props) => {
    const[activeIndex, setActiveIndex] = useState(0);
    const[animating, setAnimating] = useState(false)


    const next = () => {
        if (animating) return;
        const nextIndex = activeIndex === items.length - 1 ? 0 : activeIndex + 1;
        setActiveIndex(nextIndex);
    }

    const previous =() => {
        if (animating) return;
        const nextIndex = activeIndex === 0 ? items.length - 1 : this.state.activeIndex - 1;
        setActiveIndex(nextIndex);
    }

    const goToIndex = (newIndex) => {
        if (animating) return;
        setActiveIndex(newIndex);
    }

    const slides = items.map((item) => {
        return (
        <CarouselItem onExiting={() => setAnimating(true)} onExited={() => setAnimating(false)} key={item.src}>
            <img src={item.src} alt={item.altText} width="100%" height="750px" />
            <CarouselCaption captionText={item.caption} captionHeader={item.caption} />
        </CarouselItem>
    );
    });

    return (
        <Carousel activeIndex={activeIndex} next={this.next} previous={this.previous}>
    <CarouselIndicators items={items} activeIndex={activeIndex} onClickHandler={this.goToIndex} />
        {slides}
    <CarouselControl direction="prev" directionText="Previous" onClickHandler={this.previous} />
    <CarouselControl direction="next" directionText="Next" onClickHandler={this.next} />
    </Carousel>
    );
}

export default App;
