import React, {useState} from 'react';
import './App.css';
import NavBar from './Componentes/NavBar/NavBar'
import ItemListContainer from './Componentes/ItemListContainer/ItemListContainer';
import ItemDetailContainer from './Componentes/ItemDetailContainer/ItemDetailcontainer';


const App = () => {
  const [page, setPage] = useState({path: 'list', param:'1'})

  return (
    <div className="App">
        {/* <NavBar /> */}
        <button onClick={() => setPage({...page, path: 'list'})}>Lista</button>
        {page.path === 'list' && <ItemListContainer greeting="Bienvenidos a nuestra tienda online" handLePage={setPage}/>}
        {page.path === 'detail' && <ItemDetailContainer id={page.param}/>}
    </div>
  );
}

export default App;
